mapper.sql:
select idx , os , version , is_force , is_use , reg_date , op_name , @RNUM := @RNUM + 1 as rowNum from rd_admin.tb_app_version , (select @RNUM := 0) r where is_use = 1
and os = #{os}
order by idx desc

