mapper.insert:
/* App.xml - addAppVersion */
SELECT LAST_INSERT_ID()
insert into rd_admin.tb_app_version (idx, os, version, is_force, is_use, reg_date, op_name) values (#{idx}, #{os}, #{version}, #{is_force}, #{is_use}, current_timestamp(), #{op_name})

mapper.update:
update rd_admin.tb_app_version
set is_use = 0
where idx  <>  #{idx} and os = #{os}
