mapper.sql:
select idx , os , version , build_no , upBuild_no , is_use , reg_date , op_name as opName , @RNUM := @RNUM + 1 as rowNum from rd_admin.tb_app_version , (select @RNUM := 0) r
os = #{os}
order by idx desc

