mapper.insert:
/* App.xml - addAppVersion */
SELECT LAST_INSERT_ID()
insert into rd_admin.tb_app_version (idx, os, version, build_no, upBuild_no, is_use, reg_date, op_name, memo) values (#{idx}, #{os}, #{version}, #{build_no}, #{upBuild_no}, #{is_use}, current_timestamp(), #{opName}, #{memo})

mapper.update:
update rd_admin.tb_app_version
set is_use = 0
where idx  <>  #{idx} and os = #{os}
