mapper.select:
/* Con_BannerDao.xml - callContentsBannerList */
select
    @rownum := @rownum + 1 as rowNum,
    a.*
FROM
(


) AS a,
(select @rownum := #{pageStart}) AS b

    a.order asc, a.reg_date desc

limit  #{pageStart}, #{pageCnt}

mapper.insert:
/* Con_BannerDao.xml - callContentsBannerAdd */
INSERT INTO rd_admin.tb_admin_banner
(
      title
      ,view_type
      ,sex
      ,frequency_rate
      ,term_type
      ,start_datetime
      ,end_datetime
      ,is_pop
      ,is_view
      ,`position`
      ,pc_img_url
      ,pc_link_url
      ,mobile_img_url
      ,mobile_link_url
      ,thumb_img_url
      ,platform
      ,popup_type
      ,is_cookie
      ,contents
      ,is_title_view
      ,is_button_view
      ,`desc`
      ,ios_judge_view_on
      ,reg_date
      ,op_name
      ,last_upd_date
      ,last_op_name
      ,day_of_the_week
      ,week_time_type
      ,week_start_datetime
      ,week_end_datetime
)
VALUES
(
      #{title}
      ,#{view_type}
      ,#{sex}
      ,#{frequency_rate}
      ,#{term_type}
      ,STR_TO_DATE(#{start_datetime},'%Y%m%d %H%i%S')
      ,STR_TO_DATE(#{end_datetime},'%Y%m%d %H%i%S')
      ,#{is_pop}
      ,#{is_view}
      ,#{position}
      ,#{pc_img_url}
      ,#{pc_link_url}
      ,#{mobile_img_url}
      ,#{mobile_link_url}
      ,#{thumb_img_url}
      ,#{platform}
      ,#{popup_type}
      ,#{is_cookie}
      ,#{contents}
      ,#{is_title_view}
      ,#{is_button_view}
      ,#{desc}
      ,#{iosJudgeViewOn}
      ,NOW()
      ,#{opName}
      ,NOW()
      ,#{opName}
      ,#{dayOfTheWeek}
      ,#{content_scheduleWeekTime}
      ,concat(#{weekStartDatetime},'00')
      ,concat(#{weekEndDatetime},'59')
)

mapper.update:
/* Con_BannerDao.xml - updateOrder */
UPDATE rd_admin.tb_admin_banner
SET `order` = #{order}
where idx = #{banner_idx}
