mapper.insert:
INSERT into rd_data.tb_member_notification ( mem_no, type_noti, contents, memo, target_mem_no, room_no, read_yn, last_upd_date ) SELECT e.mem_no, #{slctType}, #{notiContents}, #{notimemo}, e.mem_no, null, 0, NOW() FROM ( SELECT a.mem_no, b.set_6 FROM tb_member_stmp_token AS a INNER JOIN tb_member_setting AS b ON a.mem_no = b.mem_no WHERE b.set_6 = '1'
AND a.os_type = #{slctType}
) AS e
