mapper.select:
/* Cus_BlockAdm.xml - selectBlockDetail*/


        select
          op_msg
          , idx as report_idx
        from rd_data.tb_member_report
        where idx = #{report_idx}


        select
          memo as adminMemo
        from rd_admin.tb_login_block
        where idx = #{idx}

mapper.insert:
/* Cus_BlockAdm.xml - insertBlock*/
insert into rd_admin.tb_login_block (
  block_type
  , block_text
  , block_day
  , block_end_date
  , last_upd_date
  , op_name
  , report_idx
  , memo
) values (
  #{radioBlock}
  , #{block_text}
  , #{block_day}


          , '9999-12-31'


          , '9999-12-30'


          , DATE_ADD(now(), interval #{block_day} day)


   , now()
   , #{op_name}
   , null
   , #{adminMemo}
)

/* Cus_BlockAdm.xml - insertBlockHistory*/
insert into rd_admin.tb_login_block_history (
  edit_contents
  , edit_type
  , last_upd_date
  , op_name
  , report_idx
  , memo
  ) values (
    #{edit_contents}
    , #{edit_type}
    , now()
    , #{op_name}
    , null
    , #{adminMemo}
  )

mapper.delete:
/* Cus_BlockAdm.xml - deleteBlock*/
delete from rd_admin.tb_login_block
where idx = #{idx}

mapper.insert:
/* Cus_BlockAdm.xml - insertDelBlockHistory*/
insert into rd_admin.tb_login_block_history (
  edit_contents
  , edit_type
  , last_upd_date
  , op_name
  , report_idx
  , memo
  ) values (
    #{edit_contents}
    , #{edit_type}
    , now()
    , #{op_name}
    , #{report_idx}
    , null
  )

mapper.select:
/* Cus_BlockAdm.xml - selectBlockInfo*/
select *
    from rd_admin.tb_login_block
    where idx = #{idx}

/* Cus_BlockAdm.xml - selectBlockHistListCnt*/
select count(*)
    from rd_admin.tb_login_block_history
