mapper.select:
select @rownum := @rownum + 1 as rowNum, c.* from ( select * From rd_data.tb_member_basic
where mem_join_date >= STR_TO_DATE(#{sDate}, '%Y%m%d') and mem_join_date < STR_TO_DATE(#{eDate}, '%Y%m%d')
where mem_join_date >= STR_TO_DATE(#{sDate}, '%Y%m%d')
)as c, (select @rownum := #{pageNo} ) AS d order by mem_join_date desc limit #{pageNo}, #{pageCnt}

select @rownum := @rownum + 1 as rowNum, c.* from ( select * From rd_data.tb_member_withdrawal_bak
where mem_join_date >= STR_TO_DATE(#{sDate}, '%Y%m%d') and mem_join_date < STR_TO_DATE(#{eDate}, '%Y%m%d')
where mem_join_date >= STR_TO_DATE(#{sDate}, '%Y%m%d')
)as c, (select @rownum := #{pageNo} ) AS d order by last_upd_date desc limit #{pageNo}, #{pageCnt}
