mapper.select:
select
    @rownum := @rownum + 1 as rowNum,
    c.*
  FROM (

  ) AS c, (select @rownum := #{pageNo} ) AS d
  order by c.last_upd_date desc
  limit #{pageNo}, #{pageCnt}

mapper.update:
update rd_data.tb_member_broadcast_notice_reply
    set status = #{status}
        , opName = #{opName}
    where idx = #{replyIdx}
