mapper.select:
/* Menu.xml - getRecommendList */ select @RNUM := @RNUM + 1 as rowNum, a.* from ( select basic.mem_no , basic.mem_id , basic.mem_nick , profile.image_profile , concat(level.level, ' / ', level.grade) as level , level.grade , wallet.gold , count(broad.idx) broad_cnt , ifnull(sum(send_item.item_cnt), 0) item_cnt , round(ifnull(sum(receive_item.item_cnt), 0) / count(broad.idx), 2) receive_average_cnt , min(start_date) first_broad_date , max(start_date) last_broad_date from tb_member_basic basic inner join tb_member_profile profile on basic.mem_no = profile.mem_no inner join tb_member_level level on basic.mem_no = level.mem_no inner join tb_member_wallet wallet on basic.mem_no = wallet.mem_no left outer join tb_member_broadcast_broadcasting first_broad on basic.mem_no = first_broad.mem_no left outer join tb_member_broadcast_basic broad on basic.mem_no = broad.mem_no left outer join tb_member_broadcast_item send_item on basic.mem_no = send_item.gifted_mem_no left outer join tb_member_broadcast_item receive_item on basic.mem_no = receive_item.mem_no where first_broad.start_date is not null group by basic.mem_no
order by broad_cnt desc, first_broad_date desc, receive_average_cnt desc
order by receive_average_cnt desc, broad_cnt desc, first_broad_date desc
) a , (SELECT @RNUM := 0) r
