mapper.select:
/* Pay_Pay.xml - getPayList */
select @rownum := @rownum + 1 as rowNum,a.*
from (

) a
where (mem_nick like concat('%', #{searchText}, '%')
or mem_no like concat('%', #{searchText}, '%')
or mem_id like concat('%', #{searchText}, '%')
or mem_phone like concat('%', #{searchText}, '%'))

    and DATE_FORMAT(pay_dt_comein, '%Y%m%d') >= STR_TO_DATE(#{sDate}, '%Y%m%d') and DATE_FORMAT(pay_dt_comein, '%Y%m%d')  <=  STR_TO_DATE(#{eDate}, '%Y%m%d')


    and DATE_FORMAT(pay_dt_comein, '%Y%m%d') >= STR_TO_DATE(#{sDate}, '%Y%m%d') and DATE_FORMAT(pay_dt_comein, '%Y%m%d')  <  STR_TO_DATE(#{eDate}, '%Y%m%d')


    and DATE_FORMAT(pay_dt_comein, '%Y%m%d') = STR_TO_DATE(#{sDate}, '%Y%m%d')



        and pay_yn ='Y'


        and cancel_state = 'Y'


        and cancel_state = 'F'



    and os = #{ostype}


    and chrgr_yn = #{innerType}


    and pay_way = #{payWay}


limit #{pageStart}, #{pageCnt}

/* Pay_Pay.xml - getPayListCnt */
select
  count(*)
from (

) a
where (mem_nick like concat('%', #{searchText}, '%')
or mem_no like concat('%', #{searchText}, '%')
or mem_id like concat('%', #{searchText}, '%')
or mem_phone like concat('%', #{searchText}, '%'))

    and DATE_FORMAT(pay_dt_comein, '%Y%m%d') >= STR_TO_DATE(#{sDate}, '%Y%m%d') and DATE_FORMAT(pay_dt_comein, '%Y%m%d')  <=  STR_TO_DATE(#{eDate}, '%Y%m%d')


    and DATE_FORMAT(pay_dt_comein, '%Y%m%d') >= STR_TO_DATE(#{sDate}, '%Y%m%d') and DATE_FORMAT(pay_dt_comein, '%Y%m%d')  <  STR_TO_DATE(#{eDate}, '%Y%m%d')


    and DATE_FORMAT(pay_dt_comein, '%Y%m%d') = STR_TO_DATE(#{sDate}, '%Y%m%d')



        and pay_yn ='Y'


        and cancel_state = 'Y'


        and cancel_state = 'F'



    and os = #{ostype}


    and chrgr_yn = #{innerType}


    and pay_way = #{payWay}

/* Pay_Pay.xml - getPaySummary */ select count(case when pay_yn = 'Y' then 1 end) as totalPayCnt ,sum(case when pay_yn = 'Y' then pay_amt end) as totalPayAmt ,count(case when cancel_state = 'Y' then 1 end) as totalPayCancelCnt ,sum(case when cancel_state = 'Y' then pay_amt end) as totalPayCancelAmt ,count(case when cancel_state = 'F' then 1 end) as totalPayCancelCannotCnt ,sum(case when cancel_state = 'F' then pay_amt end) as totalPayCancelCannotAmt from (
) a where (mem_nick like concat('%', #{searchText}, '%') or mem_no like concat('%', #{searchText}, '%') or mem_id like concat('%', #{searchText}, '%') or mem_phone like concat('%', #{searchText}, '%'))
and DATE_FORMAT(pay_dt_comein, '%Y%m%d') >= STR_TO_DATE(#{sDate}, '%Y%m%d') and DATE_FORMAT(pay_dt_comein, '%Y%m%d') <= STR_TO_DATE(#{eDate}, '%Y%m%d')
and DATE_FORMAT(pay_dt_comein, '%Y%m%d') >= STR_TO_DATE(#{sDate}, '%Y%m%d') and DATE_FORMAT(pay_dt_comein, '%Y%m%d') < STR_TO_DATE(#{eDate}, '%Y%m%d')
and DATE_FORMAT(pay_dt_comein, '%Y%m%d') = STR_TO_DATE(#{sDate}, '%Y%m%d')
and pay_yn ='Y'
and cancel_state = 'Y'
and cancel_state = 'F'
and os = #{ostype}
and chrgr_yn = #{innerType}
and pay_way = #{payWay}
