mapper.insert:
/* Sample.xml - insertSample */
    INSERT INTO TEST(
      ID
      , NAME
      , AGE
    ) VALUES (
      #{id}
      , #{name}
      , #{age}
    )

mapper.select:
/* Sample.xml - getLogErrorData */ select idx , mem_no , ostype , version , dtype , ctype , `desc` , upd_date from rd_data.tb_log_error_data
DATE_FORMAT(#{txt_startSel},'%Y-%m-%d 00:00:00') <= upd_date
and upd_date <= DATE_FORMAT(#{txt_endSel},'%Y-%m-%d 23:59:59')
and ostype = #{ostype}
order by idx desc limit 500;
