mapper.select:
/* Sta_Push.xml - callPushHistoryListCnt */ SELECT COUNT(*) FROM ( SELECT idx ,send_date ,target_mem ,target_platform ,push_slct ,push_type ,image_type ,send_type ,target_cnt ,send_title ,send_cont ,mem_no ,mem_nick ,send_etc ,send_cnt ,succ_cnt ,fail_cnt ,succ_rate ,click_cnt ,click_rate ,admin_idx ,ins_date ,last_upd_date FROM rd_stmp.smtp_push_history
send_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d 00:00:00') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d 23:59:59')
AND CONCAT('1',target_platform) = #{platform}
AND send_type = #{send_type}
AND push_slct = #{push_slct}
) AS a
AND a.send_title like concat ('%', #{searchText}, '%')
AND a.send_cont like concat ('%', #{searchText}, '%')
AND (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%'))
AND (a.send_title like concat ('%', #{searchText}, '%') OR a.send_cont like concat ('%', #{searchText}, '%') OR (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%')))

/* Sta_Push.xml - callPushHistoryList */ SELECT @rownum := @rownum + 1 as rowNum, a.* FROM ( SELECT history.idx ,history.send_date ,history.target_mem ,history.target_platform ,history.push_slct ,history.push_type ,history.image_type ,history.send_type ,history.target_cnt ,history.send_title ,history.send_cont ,IF(history.admin_idx = '0', history.mem_no, NULL) AS mem_no ,IF(history.admin_idx = '0', history.mem_nick, admin.op_name) AS mem_nick ,history.send_etc ,history.send_cnt ,history.succ_cnt ,history.fail_cnt ,history.succ_rate ,history.click_cnt ,history.click_rate ,history.admin_idx ,history.ins_date ,history.last_upd_date FROM rd_stmp.smtp_push_history AS history LEFT JOIN rd_admin.tb_admin_push AS admin ON history.admin_idx = admin.idx
history.send_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d 00:00:00') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d 23:59:59')
AND CONCAT('1',history.target_platform) = #{platform}
AND history.send_type = #{send_type}
AND history.push_slct = #{push_slct}
) AS a, (select @rownum := #{pageStart}) AS b
AND a.send_title like concat ('%', #{searchText}, '%')
AND a.send_cont like concat ('%', #{searchText}, '%')
AND (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%'))
AND (a.send_title like concat ('%', #{searchText}, '%') OR a.send_cont like concat ('%', #{searchText}, '%') OR (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%')))
a.ins_date desc
LIMIT #{pageStart}, #{pageCnt}

SELECT 'summary' AS idx , SUM(a.target_cnt) AS target_cnt , SUM(a.send_cnt) AS send_cnt , SUM(a.succ_cnt) AS succ_cnt , SUM(a.fail_cnt) AS fail_cnt , IF( SUM(a.send_cnt) > 0, FLOOR((SUM(a.succ_cnt)/SUM(a.send_cnt))*100), 0) AS succ_rate , SUM(a.click_cnt) AS click_cnt , if( SUM(a.succ_cnt) > 0 , FLOOR((SUM(a.click_cnt)/SUM(a.succ_cnt))*100), 0) AS click_rate FROM( SELECT history.idx ,history.send_date ,history.target_mem ,history.target_platform ,history.push_slct ,history.push_type ,history.image_type ,history.send_type ,history.target_cnt ,history.send_title ,history.send_cont ,IF(history.admin_idx = '0', history.mem_no, NULL) AS mem_no ,IF(history.admin_idx = '0', history.mem_nick, admin.op_name) AS mem_nick ,history.send_etc ,history.send_cnt ,history.succ_cnt ,history.fail_cnt ,history.succ_rate ,history.click_cnt ,history.click_rate ,history.admin_idx ,history.ins_date ,history.last_upd_date FROM rd_stmp.smtp_push_history AS history LEFT JOIN rd_admin.tb_admin_push AS admin ON history.admin_idx = admin.idx
history.send_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d 00:00:00') AND DATE_FORMAT(#{endDate}, '%Y-%m-%d 23:59:59')
AND CONCAT('1',history.target_platform) = #{platform}
AND history.send_type = #{send_type}
AND history.push_slct = #{push_slct}
) AS a
AND a.send_title like concat ('%', #{searchText}, '%')
AND a.send_cont like concat ('%', #{searchText}, '%')
AND (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%'))
AND (a.send_title like concat ('%', #{searchText}, '%') OR a.send_cont like concat ('%', #{searchText}, '%') OR (a.mem_no like concat ('%', #{searchText}, '%') OR a.mem_nick like concat ('%', #{searchText}, '%')))
